import React, { Component } from 'react';

class App extends Component {
  state = {
    AttractionInfo: {},
    loading: true
  }

  componentDidMount() {
    fetch('https://eftelingapi.herokuapp.com/attractions')
      .then(response => response.json())
      .then(data => this.setState({ ...data, loading: false  }))
      ;
  };

  render() {
    if (this.state.loading) {
      return (
        <p>Loading data..</p>
      );
    }
  }
}

export default App;
